(function ($) {
  'use strict';
  try {
    $('.js-contact-form').each(function () {
      var t = $(this);
      t.on('submit', function (e) {
        e.preventDefault();
        var o = 'mail.php',
          n = t.find('button[type="submit"]');
        n.prop('disabled', !0).html('Sending...'),
          $.ajax({
            type: 'POST',
            url: o,
            data: t.serialize(),
            success: function (e) {
              var o = JSON.parse(e),
                r = o.message,
                s = o.type;
              1 === s
                ? (swal('Success', r, 'success'), n.html('Sent'), t[0].reset())
                : 0 === s &&
                  (swal('Error', r, 'error'),
                  n.prop('disabled', !1).html('Send message'));
            },
            statusCode: {
              404: function () {
                swal('Oops', 'File Not Found!', 'error'),
                  n.prop('disabled', !1).html('Send message');
              },
            },
            error: function (t, e, o) {
              swal('Oops', o, 'error'),
                n.prop('disabled', !1).html('Send message');
            },
          });
      });
    });
  } catch (t) {
    console.log(t);
  }
})(jQuery);
